mapper.select:
SELECT * from TB_Phone_Purchase
WHERE phoneModel like
CONCAT('${phoneModel}','%') and 2> status
limit #{limit}
offset #{offset}
order by createtime desc;

SELECT *
from TB_Phone_Purchase
WHERE id=#{phoneid} and 2> status;

mapper.update:
UPDATE
TB_Phone_Purchase
set Status=#{Status}
WHERE
id =
#{phoneid};

mapper.select:
SELECT * from TB_Phone_Purchase
WHERE phoneCode
= #{phoneCode}
and 2> status;
